package fx.backend.admin;import fx.backend.domain.Orders;import fx.backend.service.OrderService;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.bind.annotation.*;import java.util.List;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping("/admin")public class AdminController {    private final OrderService orderService;    @PatchMapping("/update/{orderId}")    public ResponseEntity<Orders> editOrder(@PathVariable Long orderId, @Validated @ModelAttribute Orders orders) {        Orders updateOrder = orderService.updateOrder(orderId, orders);        return ResponseEntity.ok(updateOrder);    }    /**     * 요청이 들어오면 모든 order 를 반환함     *     * @return     */    @PostMapping("/find-all")    public List<Orders> findAllOrder() {        return orderService.findAll();    }    @DeleteMapping("/delete/{orderId}")    public ResponseEntity<?> deleteOrder(@PathVariable Long orderId) {        orderService.deleteOrder(orderId);        return ResponseEntity.ok().build();    }}